import React, { Component } from "react";
import RedCar from "../assets/red-car.jpg";
import BlackCar from "../assets/black-car.jpg";
import SilverCar from "../assets/silver-car.jpg";
import SteelCar from "../assets/steel-car.jpg";

export default class CarEx2 extends Component {
  state = {
    imgSoure: RedCar,
  };

  carColor = (img) => {
    this.setState({
      imgSoure: img,
    }, () => {console.log(this.state.imgSoure);});
  };

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-7">
            <img className="w-100" src={this.state.imgSoure} alt="" />
          </div>
          <div className="col-5">
            <button
              onClick={() => {
                this.carColor(RedCar);
              }}
              className="btn btn-danger"
            >
              Red
            </button>
            <button
              onClick={() => {
                this.carColor(BlackCar);
              }}
              className="btn btn-dark"
            >
              Black
            </button>
            <button
              onClick={() => {
                this.carColor(SteelCar);
              }}
              className="btn btn-secondary"
            >
              Steel
            </button>
            <button
              onClick={() => {
                this.carColor(SilverCar);
              }}
              className="btn btn-light"
            >
              Silver
            </button>
          </div>
        </div>
      </div>
    );
  }
}
